def cart2polar(dx, dy):
    dx += 1e-5
    dy += 1e-5
    arctan = math.degrees(math.atan2(dy, dx))
    return arctan + {(1, 1): 0, (-1, 1): 180, (-1, -1): 180, (1, -1): 360}.get((dx / abs(dx), dy / abs(dy)))


def encode_orientation(o_yaw):
    yaw = o_yaw
    while yaw < 0:
        yaw += 360
    while yaw > 360:
        yaw -= 360

    r = g = b = 1 + int(yaw * 254. / 360.)

    return pygame.Color(r, g, b)


def get_actor_display_name(actor, truncate=250):
    name = ' '.join(actor.type_id.replace('_', '.').title().split('.')[1:])
    return (name[:truncate - 1] + u'\u2026') if len(name) > truncate else name


class Util(object):

    @staticmethod
    def blits(destination_surface, source_surfaces, rect=None, blend_mode=0):
        for surface in source_surfaces:
            destination_surface.blit(surface[0], surface[1], rect, blend_mode)

    @staticmethod
    def length(v):
        return math.sqrt(v.x ** 2 + v.y ** 2 + v.z ** 2)

    @staticmethod
    def get_bounding_box(actor, offset_x=0, offset_y=0, scale_x=1, scale_y=1):
        bb = actor.trigger_volume.extent
        corners = [carla.Location(x=-bb.x * scale_x, y=-bb.y * scale_y),
                   carla.Location(x=bb.x * scale_x, y=-bb.y * scale_y),
                   carla.Location(x=bb.x * scale_x, y=bb.y * scale_y),
                   carla.Location(x=-bb.x * scale_x, y=bb.y * scale_y),
                   carla.Location(x=-bb.x * scale_x, y=-bb.y * scale_y)]
        corners = [carla.Location(x=offset_x, y=offset_y) + x + actor.trigger_volume.location for x in corners]
        t = actor.get_transform()
        t.transform(corners)
        return corners


