class MapImage(object):
    def __init__(self, carla_world, carla_map, pixels_per_meter, show_triggers, show_connections, show_spawn_points,
                 recompute=False):
        self._pixels_per_meter = pixels_per_meter
        self.scale = 1.0
        self.show_triggers = show_triggers
        self.show_connections = show_connections
        self.show_spawn_points = show_spawn_points

        waypoints = carla_map.generate_waypoints(2)
        margin = 50
        max_x = max(waypoints, key=lambda x: x.transform.location.x).transform.location.x + margin
        max_y = max(waypoints, key=lambda x: x.transform.location.y).transform.location.y + margin
        min_x = min(waypoints, key=lambda x: x.transform.location.x).transform.location.x - margin
        min_y = min(waypoints, key=lambda x: x.transform.location.y).transform.location.y - margin

        self.width = max(max_x - min_x, max_y - min_y)
        self._world_offset = (min_x, min_y)

        width_in_pixels = int(self._pixels_per_meter * self.width)

        self.big_map_surface = pygame.Surface((width_in_pixels, width_in_pixels)).convert()
        self.big_lane_surface = pygame.Surface((width_in_pixels, width_in_pixels)).convert()
        self.big_stop_surface = pygame.Surface((width_in_pixels, width_in_pixels)).convert()

        self.big_waypoint_surfaces = [pygame.Surface((width_in_pixels, width_in_pixels)).convert() for _ in range(6)]

        # Save static surfaces
        save_path = f'assets/maps/{carla_map.name}'
        # print(f'save_path: {save_path}, map_name: {carla_map.name}')
        if recompute:
            self.draw_road_map(
                self.big_map_surface, self.big_lane_surface, self.big_stop_surface, self.big_waypoint_surfaces,
                carla_world, carla_map, self.world_to_pixel, self.world_to_pixel_width)
        else:
            self.big_map_surface = pygame.image.load_extended(f'{save_path}/map.png')
            self.big_lane_surface = pygame.image.load_extended(f'{save_path}/lane.png')
            self.big_stop_surface = pygame.image.load_extended(f'{save_path}/stop.png')
            for i in range(6):
                self.big_waypoint_surfaces[i] = pygame.image.load_extended(f'{save_path}/waypoint{i}.png')

        if not os.path.exists(save_path):
            os.makedirs(save_path)

        if recompute:
            pygame.image.save_extended(self.big_map_surface, f'{save_path}/map.png')
            pygame.image.save_extended(self.big_lane_surface, f'{save_path}/lane.png')
            pygame.image.save_extended(self.big_stop_surface, f'{save_path}/stop.png')
            for i in range(6):
                pygame.image.save_extended(self.big_waypoint_surfaces[i], f'{save_path}/waypoint{i}.png')

        self.map_surface = self.big_map_surface
        self.lane_surface = self.big_lane_surface
        self.stop_surface = self.big_stop_surface
        self.waypoint_surfaces = self.big_waypoint_surfaces

        self.road_map = np.swapaxes(pygame.surfarray.array3d(self.big_map_surface), 0, 1)

    def draw_road_map(self, map_surface, lane_surface, stop_surface, waypoint_surfaces, carla_world, carla_map,
                      world_to_pixel, world_to_pixel_width, threshold=math.radians(1)):
        # map_surface.fill(COLOR_ALUMINIUM_4)
        map_surface.fill(COLOR_BLACK)
        lane_surface.fill(COLOR_BLACK)
        precision = 0.05

        def draw_solid_line(surface, color, closed, points, width):
            if len(points) >= 2:
                pygame.draw.lines(surface, color, closed, points, width)

        def draw_broken_line(surface, color, closed, points, width):
            broken_lines = [x for n, x in enumerate(zip(*(iter(points),) * 20)) if n % 3 == 0]
            for line in broken_lines:
                pygame.draw.lines(surface, color, closed, line, width)

        def get_lane_markings(lane_marking_type, lane_marking_color, waypoints, sign):
            margin = 0.20
            if lane_marking_type == carla.LaneMarkingType.Broken or (lane_marking_type == carla.LaneMarkingType.Solid):
                marking_1 = [world_to_pixel(lateral_shift(w.transform, sign * w.lane_width * 0.5)) for w in waypoints]
                return [(lane_marking_type, lane_marking_color, marking_1)]
            elif lane_marking_type == carla.LaneMarkingType.SolidBroken or lane_marking_type == carla.LaneMarkingType.BrokenSolid:
                marking_1 = [world_to_pixel(lateral_shift(w.transform, sign * w.lane_width * 0.5)) for w in waypoints]
                marking_2 = [world_to_pixel(lateral_shift(w.transform,
                                                          sign * (w.lane_width * 0.5 + margin * 2))) for w in waypoints]
                return [(carla.LaneMarkingType.Solid, lane_marking_color, marking_1),
                        (carla.LaneMarkingType.Broken, lane_marking_color, marking_2)]
            elif lane_marking_type == carla.LaneMarkingType.BrokenBroken:
                marking = [world_to_pixel(lateral_shift(w.transform,
                                                        sign * (w.lane_width * 0.5 - margin))) for w in waypoints]
                return [(carla.LaneMarkingType.Broken, lane_marking_color, marking)]
            elif lane_marking_type == carla.LaneMarkingType.SolidSolid:
                marking = [world_to_pixel(lateral_shift(w.transform,
                                                        sign * ((w.lane_width * 0.5) - margin))) for w in waypoints]
                return [(carla.LaneMarkingType.Solid, lane_marking_color, marking)]

            return [(carla.LaneMarkingType.NONE, carla.LaneMarkingColor.Other, [])]

        def draw_lane_marking(surface, waypoints, is_left):
            sign = -1 if is_left else 1
            lane_marking = None

            marking_type = carla.LaneMarkingType.NONE
            previous_marking_type = carla.LaneMarkingType.NONE

            marking_color = carla.LaneMarkingColor.Other
            previous_marking_color = carla.LaneMarkingColor.Other

            waypoints_list = []
            temp_waypoints = []
            current_lane_marking = carla.LaneMarkingType.NONE
            for sample in waypoints:
                lane_marking = sample.left_lane_marking if sign < 0 else sample.right_lane_marking

                if lane_marking is None:
                    continue

                marking_type = lane_marking.type
                marking_color = lane_marking.color

                if current_lane_marking != marking_type:
                    markings = get_lane_markings(
                        previous_marking_type,
                        # lane_marking_color_to_tango(previous_marking_color),
                        COLOR_WHITE,
                        temp_waypoints,
                        sign)
                    current_lane_marking = marking_type

                    for marking in markings:
                        waypoints_list.append(marking)

                    temp_waypoints = temp_waypoints[-1:]

                else:
                    temp_waypoints.append((sample))
                    previous_marking_type = marking_type
                    previous_marking_color = marking_color

            # Add last marking
            last_markings = get_lane_markings(
                previous_marking_type,
                # lane_marking_color_to_tango(previous_marking_color),
                COLOR_WHITE,
                temp_waypoints,
                sign)

            for marking in last_markings:
                waypoints_list.append(marking)

            # Once the lane markings have been simplified to Solid or Broken lines, we draw them
            for markings in waypoints_list:
                draw_solid_line(surface, markings[1], False, markings[2], 1)
                # if markings[0] == carla.LaneMarkingType.Solid:
